xrx:view.div:
			{
			let $requested-email := request:get-parameter('email', '')
			let $user := $user:db-base-collection/xrx:user[xrx:email=$requested-email]
			let $email := $user/xrx:email/text()
			let $name := user:firstname-name($email)
			let $info := $user/xrx:info/text()
			return



{ concat($name, ' (', $email, ')') }
				{ $info }

			}
