xrx:service.xrx:body:
{ (: get charter file and check for img :) let $charter-file := collection($annotation:collection-base-path-charter)//atom:id[ends-with(., $annotation:charter)]/parent::atom:entry let $imgUrl := substring-before(substring-after($data, 'imgUrl='), '?!;') let $search-img := $charter-file//cei:graphic[@url = $imgUrl]
(: get index of searched tag :) let $index := if($type = "zone")then count($charter-file//cei:zone[@id = $annotation:zone-id]/preceding-sibling::cei:zone) else if(empty($search-img))then let $short-img-name := substring-before($annotation:img-name, ".") let $search-fig := $charter-file//cei:figure[@n = $short-img-name] return if(empty($search-fig))then xs:string("Not available") else count($charter-file//cei:figure[@n = $short-img-name]/preceding-sibling::cei:figure) else count($charter-file//cei:figure[./cei:graphic[@url = $imgUrl]]/preceding-sibling::cei:figure)
return $index }
